import { Injectable } from '@angular/core';
import { Observable, Subject, BehaviorSubject } from 'rxjs';
import * as Y from 'yjs';
import { KanbanCard } from '../models/kanban-card.interface';
import { KanbanColumn } from '../models/kanban-column.interface';
import { Operation, CRDTUpdate } from '../types/crdt-types.interface';

@Injectable({
  providedIn: 'root'
})
export class CRDTService {
  private documents = new Map<string, Y.Doc>();
  private updateSubjects = new Map<string, Subject<Uint8Array>>();
  private operationHistory = new Map<string, Operation[]>();

  constructor() {}

  initializeDocument(boardId: string): Y.Doc {
    if (this.documents.has(boardId)) {
      return this.documents.get(boardId)!;
    }

    const doc = new Y.Doc();
    this.documents.set(boardId, doc);

    // Initialize shared types
    const cards = doc.getArray<KanbanCard>('cards');
    const columns = doc.getArray<KanbanColumn>('columns');
    const metadata = doc.getMap('metadata');
    const operations = doc.getArray<Operation>('operations');

    // Set up update listener
    const updateSubject = new Subject<Uint8Array>();
    this.updateSubjects.set(boardId, updateSubject);

    doc.on('update', (update: Uint8Array) => {
      updateSubject.next(update);
    });

    // Initialize operation history
    this.operationHistory.set(boardId, []);

    return doc;
  }

  getSharedArray<T>(docId: string, arrayName: string): Y.Array<T> {
    const doc = this.documents.get(docId);
    if (!doc) {
      throw new Error(`Document ${docId} not found`);
    }
    return doc.getArray<T>(arrayName);
  }

  getSharedMap<T>(docId: string, mapName: string): Y.Map<T> {
    const doc = this.documents.get(docId);
    if (!doc) {
      throw new Error(`Document ${docId} not found`);
    }
    return doc.getMap<T>(mapName);
  }

  applyUpdate(docId: string, update: Uint8Array): void {
    const doc = this.documents.get(docId);
    if (!doc) {
      throw new Error(`Document ${docId} not found`);
    }

    try {
      Y.applyUpdate(doc, update);
    } catch (error) {
      console.error(`Failed to apply update to document ${docId}:`, error);
      throw error;
    }
  }

  onUpdate(docId: string): Observable<Uint8Array> {
    const updateSubject = this.updateSubjects.get(docId);
    if (!updateSubject) {
      throw new Error(`Document ${docId} not found`);
    }
    return updateSubject.asObservable();
  }

  encodeStateAsUpdate(docId: string): Uint8Array {
    const doc = this.documents.get(docId);
    if (!doc) {
      throw new Error(`Document ${docId} not found`);
    }
    return Y.encodeStateAsUpdate(doc);
  }

  // Card operations
  addCard(boardId: string, card: KanbanCard, clientId: string): void {
    const cards = this.getSharedArray<KanbanCard>(boardId, 'cards');

    // Create operation record
    const operation: Operation = {
      id: this.generateOperationId(),
      type: 'create',
      entityType: 'card',
      entityId: card.id,
      data: card,
      timestamp: Date.now(),
      clientId,
      dependencies: []
    };

    // Add to operation history
    this.addToOperationHistory(boardId, operation);

    // Apply to CRDT
    cards.push([card]);
  }

  updateCard(boardId: string, cardId: string, updates: Partial<KanbanCard>, clientId: string): void {
    const cards = this.getSharedArray<KanbanCard>(boardId, 'cards');
    const cardIndex = cards.toArray().findIndex(card => card.id === cardId);

    if (cardIndex === -1) {
      throw new Error(`Card ${cardId} not found`);
    }

    const currentCard = cards.get(cardIndex);
    const updatedCard = { ...currentCard, ...updates, updatedAt: new Date() };

    // Create operation record
    const operation: Operation = {
      id: this.generateOperationId(),
      type: 'update',
      entityType: 'card',
      entityId: cardId,
      data: { previous: currentCard, updated: updatedCard, changes: updates },
      timestamp: Date.now(),
      clientId,
      dependencies: []
    };

    // Add to operation history
    this.addToOperationHistory(boardId, operation);

    // Apply to CRDT
    cards.delete(cardIndex, 1);
    cards.insert(cardIndex, [updatedCard]);
  }

  deleteCard(boardId: string, cardId: string, clientId: string): void {
    const cards = this.getSharedArray<KanbanCard>(boardId, 'cards');
    const cardIndex = cards.toArray().findIndex(card => card.id === cardId);

    if (cardIndex === -1) {
      throw new Error(`Card ${cardId} not found`);
    }

    const card = cards.get(cardIndex);

    // Create operation record
    const operation: Operation = {
      id: this.generateOperationId(),
      type: 'delete',
      entityType: 'card',
      entityId: cardId,
      data: card,
      timestamp: Date.now(),
      clientId,
      dependencies: []
    };

    // Add to operation history
    this.addToOperationHistory(boardId, operation);

    // Apply to CRDT
    cards.delete(cardIndex, 1);
  }

  moveCard(boardId: string, cardId: string, targetColumnId: string, targetPosition: number, clientId: string): void {
    const cards = this.getSharedArray<KanbanCard>(boardId, 'cards');
    const cardIndex = cards.toArray().findIndex(card => card.id === cardId);

    if (cardIndex === -1) {
      throw new Error(`Card ${cardId} not found`);
    }

    const card = cards.get(cardIndex);
    const previousColumnId = card.columnId;
    const previousPosition = card.position;

    const updatedCard = {
      ...card,
      columnId: targetColumnId,
      position: targetPosition,
      updatedAt: new Date()
    };

    // Create operation record
    const operation: Operation = {
      id: this.generateOperationId(),
      type: 'move',
      entityType: 'card',
      entityId: cardId,
      data: {
        card: updatedCard,
        previousColumnId,
        previousPosition,
        targetColumnId,
        targetPosition
      },
      timestamp: Date.now(),
      clientId,
      dependencies: []
    };

    // Add to operation history
    this.addToOperationHistory(boardId, operation);

    // Apply to CRDT
    cards.delete(cardIndex, 1);
    cards.insert(cardIndex, [updatedCard]);
  }

  // Column operations
  addColumn(boardId: string, column: KanbanColumn, clientId: string): void {
    const columns = this.getSharedArray<KanbanColumn>(boardId, 'columns');

    // Create operation record
    const operation: Operation = {
      id: this.generateOperationId(),
      type: 'create',
      entityType: 'column',
      entityId: column.id,
      data: column,
      timestamp: Date.now(),
      clientId,
      dependencies: []
    };

    // Add to operation history
    this.addToOperationHistory(boardId, operation);

    // Apply to CRDT
    columns.push([column]);
  }

  updateColumn(boardId: string, columnId: string, updates: Partial<KanbanColumn>, clientId: string): void {
    const columns = this.getSharedArray<KanbanColumn>(boardId, 'columns');
    const columnIndex = columns.toArray().findIndex(column => column.id === columnId);

    if (columnIndex === -1) {
      throw new Error(`Column ${columnId} not found`);
    }

    const currentColumn = columns.get(columnIndex);
    const updatedColumn = { ...currentColumn, ...updates };

    // Create operation record
    const operation: Operation = {
      id: this.generateOperationId(),
      type: 'update',
      entityType: 'column',
      entityId: columnId,
      data: { previous: currentColumn, updated: updatedColumn, changes: updates },
      timestamp: Date.now(),
      clientId,
      dependencies: []
    };

    // Add to operation history
    this.addToOperationHistory(boardId, operation);

    // Apply to CRDT
    columns.delete(columnIndex, 1);
    columns.insert(columnIndex, [updatedColumn]);
  }

  // Utility methods
  getCards(boardId: string): KanbanCard[] {
    const cards = this.getSharedArray<KanbanCard>(boardId, 'cards');
    return cards.toArray();
  }

  getColumns(boardId: string): KanbanColumn[] {
    const columns = this.getSharedArray<KanbanColumn>(boardId, 'columns');
    return columns.toArray();
  }

  getOperationHistory(boardId: string): Operation[] {
    return this.operationHistory.get(boardId) || [];
  }

  private addToOperationHistory(boardId: string, operation: Operation): void {
    const history = this.operationHistory.get(boardId) || [];
    history.push(operation);
    this.operationHistory.set(boardId, history);

    // Also add to CRDT operations array for persistence
    const operations = this.getSharedArray<Operation>(boardId, 'operations');
    operations.push([operation]);
  }

  private generateOperationId(): string {
    return `op_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  // Cleanup
  destroyDocument(boardId: string): void {
    const doc = this.documents.get(boardId);
    if (doc) {
      doc.destroy();
      this.documents.delete(boardId);
    }

    const updateSubject = this.updateSubjects.get(boardId);
    if (updateSubject) {
      updateSubject.complete();
      this.updateSubjects.delete(boardId);
    }

    this.operationHistory.delete(boardId);
  }
}

import { Injectable } from '@angular/core';
import { Observable, BehaviorSubject, Subject, combineLatest } from 'rxjs';
import { map, filter, tap, catchError } from 'rxjs/operators';
import { CRDTService } from './crdt.service';
import { WebSocketService } from './websocket.service';
import {
  KanbanCard,
  KanbanColumn,
  KanbanBoardState,
  CreateKanbanCardRequest,
  UpdateKanbanCardRequest,
  KanbanValidation
} from '../models';
import {
  KanbanUpdateEvent,
  ConnectionStatus,
  CardMoveEvent,
  CardCreateEvent,
  CardUpdateEvent,
  CardDeleteEvent
} from '../types';

@Injectable({
  providedIn: 'root'
})
export class KanbanService {
  private boardStates = new Map<string, BehaviorSubject<KanbanBoardState>>();
  private updateEvents = new Subject<KanbanUpdateEvent>();

  constructor(
    private crdtService: CRDTService,
    private webSocketService: WebSocketService
  ) {
    this.setupWebSocketMessageHandling();
  }

  // Board State Management
  getBoardState(boardId: string): Observable<KanbanBoardState> {
    if (!this.boardStates.has(boardId)) {
      this.initializeBoardState(boardId);
    }
    return this.boardStates.get(boardId)!.asObservable();
  }

  updateBoardState(boardId: string, state: Partial<KanbanBoardState>): void {
    const currentStateSubject = this.boardStates.get(boardId);
    if (!currentStateSubject) {
      throw new Error(`Board ${boardId} not initialized`);
    }

    const currentState = currentStateSubject.value;
    const updatedState: KanbanBoardState = {
      ...currentState,
      ...state,
      lastModified: new Date(),
      version: currentState.version + 1
    };

    // Validate the updated state
    const validationErrors = KanbanValidation.validateBoardState(updatedState);
    if (validationErrors.length > 0) {
      throw new Error(`Invalid board state: ${validationErrors.join(', ')}`);
    }

    currentStateSubject.next(updatedState);
  }

  private initializeBoardState(boardId: string): void {
    // Initialize CRDT document
    this.crdtService.initializeDocument(boardId);

    // Create initial board state
    const initialState: KanbanBoardState = {
      id: boardId,
      title: `Board ${boardId}`,
      columns: [],
      cards: [],
      lastModified: new Date(),
      version: 0
    };

    const stateSubject = new BehaviorSubject<KanbanBoardState>(initialState);
    this.boardStates.set(boardId, stateSubject);

    // Subscribe to CRDT updates
    this.crdtService.onUpdate(boardId).subscribe(update => {
      this.syncBoardStateFromCRDT(boardId);
    });
  }

  private syncBoardStateFromCRDT(boardId: string): void {
    const cards = this.crdtService.getCards(boardId);
    const columns = this.crdtService.getColumns(boardId);

    const currentStateSubject = this.boardStates.get(boardId);
    if (!currentStateSubject) return;

    const currentState = currentStateSubject.value;
    const updatedState: KanbanBoardState = {
      ...currentState,
      cards: cards.sort((a, b) => a.position - b.position),
      columns: columns.sort((a, b) => a.position - b.position),
      lastModified: new Date(),
      version: currentState.version + 1
    };

    currentStateSubject.next(updatedState);
  }

  // Card Operations
  createCard(boardId: string, columnId: string, cardRequest: CreateKanbanCardRequest): Observable<KanbanCard> {
    return new Observable<KanbanCard>(observer => {
      try {
        // Validate request
        const validationErrors = KanbanValidation.validateCreateCardRequest(cardRequest);
        if (validationErrors.length > 0) {
          observer.error(new Error(`Invalid card data: ${validationErrors.join(', ')}`));
          return;
        }

        // Get current cards in column to determine position
        const currentCards = this.crdtService.getCards(boardId)
          .filter(card => card.columnId === columnId);
        const position = currentCards.length;

        // Create card
        const card: KanbanCard = {
          id: this.generateCardId(),
          ...cardRequest,
          columnId,
          position,
          createdAt: new Date(),
          updatedAt: new Date(),
          createdBy: this.webSocketService.getClientId()
        };

        // Add to CRDT
        this.crdtService.addCard(boardId, card, this.webSocketService.getClientId());

        // Send update via WebSocket
        this.sendCardUpdate(boardId, 'card_created', card);

        // Emit event
        const createEvent: CardCreateEvent = { card, columnId };
        this.updateEvents.next({
          type: 'card_created',
          boardId,
          data: createEvent,
          operationId: this.generateOperationId(),
          timestamp: Date.now(),
          clientId: this.webSocketService.getClientId()
        });

        observer.next(card);
        observer.complete();
      } catch (error) {
        observer.error(error);
      }
    });
  }

  updateCard(boardId: string, cardId: string, updates: UpdateKanbanCardRequest): Observable<KanbanCard> {
    return new Observable<KanbanCard>(observer => {
      try {
        // Get current card
        const currentCards = this.crdtService.getCards(boardId);
        const currentCard = currentCards.find(card => card.id === cardId);

        if (!currentCard) {
          observer.error(new Error(`Card ${cardId} not found`));
          return;
        }

        // Apply updates
        const updatedCard: KanbanCard = {
          ...currentCard,
          ...updates,
          updatedAt: new Date()
        };

        // Validate updated card
        const validationErrors = KanbanValidation.validateCard(updatedCard);
        if (validationErrors.length > 0) {
          observer.error(new Error(`Invalid card data: ${validationErrors.join(', ')}`));
          return;
        }

        // Update in CRDT
        this.crdtService.updateCard(boardId, cardId, updates, this.webSocketService.getClientId());

        // Send update via WebSocket
        this.sendCardUpdate(boardId, 'card_updated', updatedCard);

        // Emit event
        const updateEvent: CardUpdateEvent = {
          cardId,
          previousCard: currentCard,
          updatedCard,
          changes: updates
        };
        this.updateEvents.next({
          type: 'card_updated',
          boardId,
          data: updateEvent,
          operationId: this.generateOperationId(),
          timestamp: Date.now(),
          clientId: this.webSocketService.getClientId()
        });

        observer.next(updatedCard);
        observer.complete();
      } catch (error) {
        observer.error(error);
      }
    });
  }

  deleteCard(boardId: string, cardId: string): Observable<void> {
    return new Observable<void>(observer => {
      try {
        // Get current card
        const currentCards = this.crdtService.getCards(boardId);
        const card = currentCards.find(c => c.id === cardId);

        if (!card) {
          observer.error(new Error(`Card ${cardId} not found`));
          return;
        }

        // Delete from CRDT
        this.crdtService.deleteCard(boardId, cardId, this.webSocketService.getClientId());

        // Send update via WebSocket
        this.sendCardUpdate(boardId, 'card_deleted', { cardId });

        // Emit event
        const deleteEvent: CardDeleteEvent = {
          cardId,
          card,
          columnId: card.columnId
        };
        this.updateEvents.next({
          type: 'card_deleted',
          boardId,
          data: deleteEvent,
          operationId: this.generateOperationId(),
          timestamp: Date.now(),
          clientId: this.webSocketService.getClientId()
        });

        observer.next();
        observer.complete();
      } catch (error) {
        observer.error(error);
      }
    });
  }

  moveCard(boardId: string, cardId: string, targetColumnId: string, targetPosition: number): Observable<void> {
    return new Observable<void>(observer => {
      try {
        // Get current card
        const currentCards = this.crdtService.getCards(boardId);
        const card = currentCards.find(c => c.id === cardId);

        if (!card) {
          observer.error(new Error(`Card ${cardId} not found`));
          return;
        }

        const sourceColumnId = card.columnId;
        const sourcePosition = card.position;

        // Update positions of other cards
        this.updateCardPositionsAfterMove(boardId, cardId, sourceColumnId, targetColumnId, targetPosition);

        // Move card in CRDT
        this.crdtService.moveCard(boardId, cardId, targetColumnId, targetPosition, this.webSocketService.getClientId());

        // Send update via WebSocket
        this.sendCardUpdate(boardId, 'card_moved', {
          cardId,
          sourceColumnId,
          targetColumnId,
          sourcePosition,
          targetPosition
        });

        // Emit event
        const moveEvent: CardMoveEvent = {
          cardId,
          sourceColumnId,
          targetColumnId,
          sourcePosition,
          targetPosition,
          card: { ...card, columnId: targetColumnId, position: targetPosition }
        };
        this.updateEvents.next({
          type: 'card_moved',
          boardId,
          data: moveEvent,
          operationId: this.generateOperationId(),
          timestamp: Date.now(),
          clientId: this.webSocketService.getClientId()
        });

        observer.next();
        observer.complete();
      } catch (error) {
        observer.error(error);
      }
    });
  }

  private updateCardPositionsAfterMove(
    boardId: string,
    movedCardId: string,
    sourceColumnId: string,
    targetColumnId: string,
    targetPosition: number
  ): void {
    const allCards = this.crdtService.getCards(boardId);

    if (sourceColumnId === targetColumnId) {
      // Reordering within same column
      const columnCards = allCards
        .filter(card => card.columnId === sourceColumnId && card.id !== movedCardId)
        .sort((a, b) => a.position - b.position);

      columnCards.forEach((card, index) => {
        const newPosition = index >= targetPosition ? index + 1 : index;
        if (card.position !== newPosition) {
          this.crdtService.updateCard(boardId, card.id, { position: newPosition }, this.webSocketService.getClientId());
        }
      });
    } else {
      // Moving between columns
      // Update positions in source column
      const sourceCards = allCards
        .filter(card => card.columnId === sourceColumnId && card.id !== movedCardId)
        .sort((a, b) => a.position - b.position);

      sourceCards.forEach((card, index) => {
        if (card.position !== index) {
          this.crdtService.updateCard(boardId, card.id, { position: index }, this.webSocketService.getClientId());
        }
      });

      // Update positions in target column
      const targetCards = allCards
        .filter(card => card.columnId === targetColumnId)
        .sort((a, b) => a.position - b.position);

      targetCards.forEach((card, index) => {
        const newPosition = index >= targetPosition ? index + 1 : index;
        if (card.position !== newPosition) {
          this.crdtService.updateCard(boardId, card.id, { position: newPosition }, this.webSocketService.getClientId());
        }
      });
    }
  }

  // Real-time Synchronization
  subscribeToUpdates(boardId: string): Observable<KanbanUpdateEvent> {
    return this.updateEvents.asObservable().pipe(
      filter(event => event.boardId === boardId)
    );
  }

  applyRemoteUpdate(boardId: string, update: KanbanUpdateEvent): void {
    // Apply the update to local CRDT without triggering WebSocket send
    try {
      switch (update.type) {
        case 'card_created':
          const createData = update.data as CardCreateEvent;
          this.crdtService.addCard(boardId, createData.card, update.clientId);
          break;
        case 'card_updated':
          const updateData = update.data as CardUpdateEvent;
          this.crdtService.updateCard(boardId, updateData.cardId, updateData.changes, update.clientId);
          break;
        case 'card_deleted':
          const deleteData = update.data as CardDeleteEvent;
          this.crdtService.deleteCard(boardId, deleteData.cardId, update.clientId);
          break;
        case 'card_moved':
          const moveData = update.data as CardMoveEvent;
          this.crdtService.moveCard(boardId, moveData.cardId, moveData.targetColumnId, moveData.targetPosition, update.clientId);
          break;
      }
    } catch (error) {
      console.error('Failed to apply remote update:', error);
    }
  }

  // WebSocket Integration
  connectToBoard(boardId: string): Observable<ConnectionStatus> {
    return this.webSocketService.connect(boardId);
  }

  getConnectionStatus(): Observable<ConnectionStatus> {
    return this.webSocketService.getConnectionStatus();
  }

  private setupWebSocketMessageHandling(): void {
    this.webSocketService.onMessage().subscribe(message => {
      if (message.type === 'update') {
        const updateEvent = message.payload as KanbanUpdateEvent;
        this.applyRemoteUpdate(message.boardId, updateEvent);
      }
    });
  }

  private sendCardUpdate(boardId: string, type: string, data: any): void {
    if (this.webSocketService.isConnected()) {
      this.webSocketService.sendUpdate(boardId, {
        type,
        data,
        timestamp: Date.now(),
        clientId: this.webSocketService.getClientId()
      });
    }
  }

  // Utility methods
  private generateCardId(): string {
    return `card_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private generateOperationId(): string {
    return `op_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  // Cleanup
  disconnectFromBoard(): void {
    this.webSocketService.disconnect();
  }

  destroyBoard(boardId: string): void {
    const stateSubject = this.boardStates.get(boardId);
    if (stateSubject) {
      stateSubject.complete();
      this.boardStates.delete(boardId);
    }
    this.crdtService.destroyDocument(boardId);
  }
}

